private void TampilkanBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_TampilkanBTActionPerformed
// TODO add your handling code here:
//mengambil nilai dari textfield nama dan alamat
    String nama = NamaTF.getText();
    String alamat = AlamatTF.getText();
    //mengambil nilai dari combobox pekerjaan, jenis kelamin, dan jenis cetak
    String pekerjaan = (String) PekerjaanCB.getSelectedItem();
    String jeniskelamin = (String) JenisKelaminCB.getSelectedItem();
    String jeniscetak = (String) TebalCB.getSelectedItem();
    //buat fngsi font
    Font tebal = new Font ("Arial", Font.BOLD, 12);
    Font miring = new Font ("Arial", Font.ITALIC,12);
    //set hasil efek font ke text area
    if (jeniscetak == "Tebal"){
        jTextArea1.setFont(tebal);
    }
    if (jeniscetak == "Miring"){
        jTextArea1.setFont(miring);
    }
    
    jTextArea1.setText ("Nama Saya" + nama + " \n alamat saya tinggal " + alamat + " \n dengan pekerjaan " +  pekerjaan + "\n jenis kelamin saya " + jeniskelamin);
    
}//GEN-LAST:event_TampilkanBTActionPerformed

private void SimpanBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SimpanBTActionPerformed
// TODO add your handling code here:
        try{
        //buatfile
        BufferedWriter out = new BufferedWriter(new FileWriter("Penyimpanan.txt"));
        //simpan ke file
        out.write(jTextArea1.getText());
        //tampilan dialog telah berhasil
        JOptionPane.showMessageDialog(null, "Berhasil disimpan ke dalam file");
        //tutup output stream
        out.close();
        
    }catch(Exception e){
        System.err.println("Error :" + e.getMessage());
    }
}//GEN-LAST:event_SimpanBTActionPerformed

